import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';

interface ApiResponse {
  success: boolean;
  message: string;
}

@Component({
  selector: 'app-product-catalog',
  templateUrl: './product-catalog.component.html',
  styleUrls: ['./product-catalog.component.css']
})
export class ProductCatalogComponent implements OnInit {
  products: any[] = [];
  category: string = '';
  page: number = 1;
  count: number = 0;
  perPage: number = 4;
  pages: number = 0;
  currentPage: number = 1;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.loadProducts();
  }

  loadProducts() {
    let params = new HttpParams();
    params = params.set('category', this.category);
    params = params.set('page', this.page.toString());

    this.getProducts(params).subscribe(response => {
      this.products = response.products;
      this.count = response.count;
      this.pages = Math.ceil(this.count / this.perPage);
    });
  }

  filterProducts() {
    this.page = 1;
    this.loadProducts();
  }

  changePage(page: number) {
    this.page = page; // Update the current page
    this.currentPage = page; // Update the active page in the UI
    this.loadProducts();
  }
  
  getProducts(params: HttpParams): Observable<any> {
    return this.http.get<any>('http://localhost/e-commerce%20store/backend/browseProducts.php', { params: params });
  }

  getPagesArray(): number[] {
    return Array.from({ length: Math.ceil(this.count / this.perPage) }, (_, i) => i + 1);
  }
  
  addToCart(product: any) {
    const url = 'http://localhost/e-commerce%20store/backend/addProductToCart.php';
    const data = { productName: product.NAME, productCategory: product.CATEGORY };

    this.http.post<ApiResponse>(url, data).subscribe(response => {
      if (response.success) {
        alert(response.message);
      } else {
        alert('Error: ' + response.message);
      }
    });
  }

}
<div id="content">
  <nav>
    <a routerLink="/browseProducts">Browse Products</a>
    <a routerLink="/browseBasket">See basket</a>
  </nav>
  <br>
  <h1>Product Catalog</h1>

  <!-- product name filter -->
  <input type="text" [(ngModel)]="category" placeholder="Enter product name">
  <button (click)="filterProducts()">Filter</button>
  <br><br>

  <!-- product list -->
  <div id="product-list">
    <div *ngFor="let product of products">
      <h3>{{ product.NAME }}</h3>
      <p>{{ product.CATEGORY }}</p>
      <p>{{ product.PRICE }}</p>
      <button (click)="addToCart(product)">Add to Cart</button>
    </div>
  </div>
  

  <div *ngIf="pages > 1" id="pagination">
    <button *ngFor="let p of getPagesArray()" [disabled]="p === currentPage" (click)="changePage(p)">{{ p }}</button>
  </div>
  <router-outlet></router-outlet>
</div>
