import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ProductCatalogComponent } from './features/components/browseProducts/product-catalog.component';



@Component({
    selector: 'app-product-basket',
    templateUrl: './product-basket.component.html',
    styleUrls: ['./product-basket.component.css']
})
export class ProductBasketComponent implements OnInit {
    products: any[] = [];
    
    constructor(private http: HttpClient){ }

    ngOnInit() {
        this.loadProducts();
    }

    loadProducts(){
        let params = new HttpParams();
        this.getProducts(params).subscribe(response => {
            this.products = response;
        });
    }

    getProducts(params: HttpParams): Observable<any>{
        return this.http.get<any>('http://localhost/e-commerce%20store/backend/browseProducts.php', { params: params });
    }
}
